# Time dilation example in Python
def time_dilation(t, v, c=299792458):
    from math import sqrt
    return t / sqrt(1 - (v**2 / c**2))

// Calculate Lorentz factor in JavaScript
function lorentzFactor(v, c = 299792458) {
    return 1 / Math.sqrt(1 - (v * v) / (c * c));
}

